import React, { useState } from 'react';
import axios from 'axios';
import { ArrowPathIcon, CloudArrowDownIcon } from '@heroicons/react/24/outline';
import youtubeLogo from './assets/youtube_logo.png';
import backgroundVideo from './assets/background_video.mp4';

export default function App() {
  const [url, setUrl] = useState('');
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloading, setDownloading] = useState(false);
  const [error, setError] = useState('');

  const fetchComments = async () => {
    if (!url) {
      setError('Please enter a valid YouTube URL.');
      return;
    }
    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`http://localhost:8000/comments?url=${encodeURIComponent(url)}`);
      setComments(response.data.comments);
    } catch (err) {
      setError(err.response?.data?.detail || 'Could not fetch comments. Please check the URL and try again.');
      setComments([]);
    } finally {
      setLoading(false);
    }
  };

  const downloadCSV = async () => {
    if (!url) {
      setError('Please enter a YouTube URL.');
      return;
    }
    setDownloading(true);
    try {
      const response = await axios.get(
        `http://localhost:8000/download_csv?url=${encodeURIComponent(url)}`,
        { responseType: 'blob' }
      );
      const urlBlob = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = urlBlob;
      link.setAttribute('download', 'youtube_comments.csv');
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (err) {
      setError('Failed to download CSV.');
    } finally {
      setDownloading(false);
    }
  };

  const getSentimentColor = (sentiment) => {
    switch (sentiment.toLowerCase()) {
      case 'positive':
        return 'bg-green-100 text-green-700 border-green-300';
      case 'negative':
        return 'bg-red-100 text-red-700 border-red-300';
      default:
        return 'bg-yellow-100 text-yellow-700 border-yellow-300';
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 font-inter relative">
      {/* Blurred Video Background */}
      <div className="absolute inset-0 -z-10">
        <video
          autoPlay
          loop
          muted
          className="w-full h-full object-cover"
          aria-hidden="true"
        >
          <source src={backgroundVideo} type="video/mp4" />
        </video>
        {/* Dark overlay & blur */}
        <div className="absolute inset-0 bg-black/40 backdrop-blur-md" style={{backdropFilter: 'blur(8px)'}} />
      </div>

      {/* Main Card */}
      <div
        className="w-[90%] max-w-[400px] rounded-[20px] bg-white/90 shadow-xl p-8 flex flex-col items-center animate-fadeIn"
        style={{
          boxShadow: '0 8px 30px rgba(0,0,0,0.2)',
          fontFamily: "'Inter', sans-serif"
        }}
      >
        {/* Logo */}
        <img src={youtubeLogo} alt="YouTube Logo" className="w-32 mb-4 rounded-xl shadow" />
        {/* Title */}
        <h1 className="text-2xl font-bold text-gray-900 mb-6 text-center">Comments Viewer</h1>
        {/* Input */}
        <input
          type="text"
          aria-label="YouTube Video URL"
          value={url}
          onChange={e => setUrl(e.target.value)}
          placeholder="https://www.youtube.com/watch?v=..."
          className="w-full mb-4 px-4 py-3 rounded-[10px] border border-gray-200 focus:border-red-400 focus:ring-2 focus:ring-red-200 transition-all duration-200 placeholder-gray-400 text-gray-900 outline-none"
          onKeyDown={e => e.key === 'Enter' && fetchComments()}
        />
        {/* Fetch Button */}
        <button
          aria-label="Fetch Comments"
          onClick={fetchComments}
          disabled={loading}
          className="w-full py-3 rounded-[10px] font-bold text-white text-lg shadow transition-all duration-200
            bg-gradient-to-r from-[#ff0000] to-[#cc0000]
            hover:scale-105 hover:shadow-lg active:scale-95
            focus:outline-none focus:ring-2 focus:ring-red-300"
        >
          {loading ? (
            <span className="flex items-center justify-center">
              <ArrowPathIcon className="h-5 w-5 animate-spin mr-2" />
              Loading...
            </span>
          ) : (
            'Fetch Comments'
          )}
        </button>
        {/* Error Toast */}
        {error && (
          <div
            className="mt-4 w-full bg-red-100 text-red-700 rounded-lg px-4 py-2 text-sm text-center shadow animate-slideDown"
            role="alert"
          >
            {error}
          </div>
        )}
      </div>
    </div>
  );
}
