import React, { useState } from 'react';
import axios from 'axios';
import { ArrowPathIcon, CloudArrowDownIcon } from '@heroicons/react/24/outline';

function App() {
  const [url, setUrl] = useState('');
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloading, setDownloading] = useState(false);
  const [error, setError] = useState('');

  const fetchComments = async () => {
    if (!url) {
      setError('Please enter a YouTube URL');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`http://localhost:8000/comments?url=${encodeURIComponent(url)}`);
      setComments(response.data.comments);
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to fetch comments');
      setComments([]);
    } finally {
      setLoading(false);
    }
  };

  const downloadCSV = async () => {
    if (!url) {
      setError('Please enter a YouTube URL');
      return;
    }

    setDownloading(true);
    try {
      const response = await axios.get(
        `http://localhost:8000/download_csv?url=${encodeURIComponent(url)}`,
        { responseType: 'blob' }
      );
      
      // Create blob link to download
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'youtube_comments.csv');
      
      // Append to html link element page
      document.body.appendChild(link);
      
      // Start download
      link.click();
      
      // Clean up and remove the link
      link.parentNode.removeChild(link);
    } catch (err) {
      setError('Failed to download CSV');
    } finally {
      setDownloading(false);
    }
  };

  const getSentimentColor = (sentiment) => {
    switch (sentiment.toLowerCase()) {
      case 'positive':
        return 'text-green-400';
      case 'negative':
        return 'text-red-400';
      default:
        return 'text-yellow-400';
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden bg-black">
      {/* Video Background */}
      <div className="absolute inset-0 z-0">
        <div className="absolute inset-0 bg-gradient-to-b from-black/70 to-black/50 z-10"></div>
        <video
          autoPlay
          loop
          muted
          className="w-full h-full object-cover opacity-30"
        >
          <source src="https://assets.mixkit.co/videos/preview/mixkit-typing-on-a-keyboard-in-an-office-environment-41493-large.mp4" type="video/mp4" />
        </video>
      </div>

      <div className="relative z-10 py-6 flex flex-col justify-center sm:py-12">
        <div className="relative px-4 py-10 sm:max-w-xl md:max-w-3xl mx-auto">
          <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl p-8">
            <div className="max-w-md mx-auto">
              {/* YouTube Logo */}
              <div className="flex justify-center mb-8">
                <div className="bg-red-600 rounded-2xl p-4 inline-flex items-center">
                  <svg className="w-48" viewBox="0 0 90 20" preserveAspectRatio="xMidYMid meet" focusable="false">
                    <g viewBox="0 0 90 20" preserveAspectRatio="xMidYMid meet">
                      <path d="M27.9727 3.12324C27.6435 1.89323 26.6768 0.926623 25.4468 0.597366C23.2197 2.24288e-07 14.285 0 14.285 0C14.285 0 5.35042 2.24288e-07 3.12323 0.597366C1.89323 0.926623 0.926623 1.89323 0.597366 3.12324C2.24288e-07 5.35042 0 10 0 10C0 10 2.24288e-07 14.6496 0.597366 16.8768C0.926623 18.1068 1.89323 19.0734 3.12323 19.4026C5.35042 20 14.285 20 14.285 20C14.285 20 23.2197 20 25.4468 19.4026C26.6768 19.0734 27.6435 18.1068 27.9727 16.8768C28.5701 14.6496 28.5701 10 28.5701 10C28.5701 10 28.5677 5.35042 27.9727 3.12324Z" fill="#FF0000"></path>
                      <path d="M11.4253 14.2854L18.8477 10.0004L11.4253 5.71533V14.2854Z" fill="white"></path>
                    </g>
                  </svg>
                </div>
              </div>

              <h1 className="text-3xl font-bold text-center mb-8 text-white">Comments Viewer</h1>
              
              <div className="flex flex-col space-y-4">
                <input
                  type="text"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  placeholder="Enter YouTube video URL"
                  className="px-6 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:ring-2 focus:ring-red-500 text-white placeholder-white/50"
                />
                
                <div className="flex gap-4">
                  <button
                    onClick={fetchComments}
                    disabled={loading}
                    className="flex-1 px-6 py-3 bg-red-600 text-white rounded-xl hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 disabled:opacity-50 transition-all duration-300"
                  >
                    {loading ? (
                      <div className="flex items-center justify-center">
                        <ArrowPathIcon className="h-5 w-5 animate-spin mr-2" />
                        Loading...
                      </div>
                    ) : (
                      'Fetch Comments'
                    )}
                  </button>

                  {comments.length > 0 && (
                    <button
                      onClick={downloadCSV}
                      disabled={downloading}
                      className="px-6 py-3 bg-gray-600 text-white rounded-xl hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 disabled:opacity-50 transition-all duration-300 flex items-center"
                    >
                      {downloading ? (
                        <ArrowPathIcon className="h-5 w-5 animate-spin" />
                      ) : (
                        <CloudArrowDownIcon className="h-5 w-5" />
                      )}
                    </button>
                  )}
                </div>
              </div>

              {error && (
                <div className="mt-4 text-red-400 text-sm text-center">{error}</div>
              )}

              {comments.length > 0 && (
                <div className="mt-8">
                  <h2 className="text-xl font-semibold mb-4 text-white">Comments ({comments.length})</h2>
                  <div className="space-y-4 max-h-96 overflow-y-auto custom-scrollbar">
                    {comments.map((comment, index) => (
                      <div
                        key={index}
                        className="p-4 bg-white/5 backdrop-blur rounded-xl transition-all duration-300 hover:bg-white/10"
                      >
                        <div className="flex justify-between items-start gap-4">
                          <p className="text-white/90 flex-1">{comment.comment}</p>
                          <span className={`px-3 py-1 rounded-full text-sm font-medium ${getSentimentColor(comment.sentiment)}`}>
                            {comment.sentiment}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
